public class AutoClearOption {

    private int size;       // 清理大小阈值，单位:MB
    private boolean sizeOn; // 是否开启按大小清理
    private int time;       // 清理时间阈值，单位:周
    private boolean timeOn; // 是否开启按时间清理

    public int getSize() {
        return size;
    }

    public void setSize(int size) {
        this.size = size;
    }

    public boolean isSizeOn() {
        return sizeOn;
    }

    public void setSizeOn(boolean sizeOn) {
        this.sizeOn = sizeOn;
    }

    public int getTime() {
        return time;
    }

    public void setTime(int time) {
        this.time = time;
    }

    public boolean isTimeOn() {
        return timeOn;
    }

    public void setTimeOn(boolean timeOn) {
        this.timeOn = timeOn;
    }
}
